import React, { useEffect, useState } from 'react';
import {useSelector, useDispatch} from 'react-redux';
import { saveProduct, listProducts, deleteProduct } from '../actions/productActions';

function ProductsScreen(props){

  const [modalVisable, setModalVisable] = useState(false);
  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [image, setImage] = useState('');
  const [brand, setBrand] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [countInStock, setCountInStock] = useState('');
  const [description, setDescription] = useState('');
  const {loading, products, error} = productList;
  const productSave = useSelector(state => state.productSave);
  const {loading: loadingSave, success: successSave, error: errorSave} = productSave;

  const productDelete = useSelector(state => state.productDelete);
  const {loading: loadingDelete, success: successDelete, error: errorDelete} = productDelete;

  const productList = useSelector(state => state.productList);
  const {loading: loadingList, success: successList, error: errorList} = productList;
  const dispatch = useDispatch();



  useEffect(() => {
    if(successSave){
      setModalVisable(false);
    }
    dispatch(listProducts())
    return() => {
      //
    };
  }, [successSave, successDelete]);

const openModal = (product) => {
  setModalVisable(true)
  setId(product._id);
  setName(product.name);
  setPrice(product.price);
  setImage(product.image);
  description(product.description);
  setBrand(product.brand);
  setCategory(product.category);
  setCountInStock(product.countInStock);
}

const submitHandler = (e) => {
  e.preventDefault();
  dispatch(saveProduct({ _id: id, name,price, image, brand, category, countInStock, description, }));
}

const deleteHandler = (product) => {
  dispatch(deleteProduct(product._id));
}
  return  <div className= "content content-margined">
    <div className = "product-header">
      <h3> Products</h3>
      <button onClick={() => openModal({})}> Create product </button>
    </div>
    {modalVisable &&
  <div className="form">
    <form onSubmit={submitHandler}>
      <ul className="form-container">
        <li>
          <h2>Create a new Product </h2>
        </li>
        <li>
          {loadingSave && <div>Loading...</div>}
          {errorSave && <div>{errorSave}</div>}
        </li>
        <li>
          <label htmlFor="name">
            Name of Product:
          </label>
          <input type="text" name="name" value={name} id="name" onChange={(e) => setName(e.target.value)}>
          </input>
        </li>
        <li>
          <label htmlFor="price">
            Price:
          </label>
          <input type="text" name="price" value={price} id="price" onChange={(e) => setPrice(e.target.value)}>
          </input>
        </li>
        <li>
          <label htmlFor="image">
            Image:
          </label>
          <input type="text" name="image" value={image} id="image" onChange={(e) => setImage(e.target.value)}>
          </input>
        </li>
        <li>
          <label htmlFor="brand">
            Brand:
          </label>
          <input type="text" name="brand" value={brand} id="brand" onChange={(e) => setBrand(e.target.value)}>
          </input>
        </li>
        <li>
          <label htmlFor="countInStock">
            In Stock:
          </label>
          <input type="text" name="countInStock" value={countInStock} id="countInStock" onChange={(e) => setName(e.target.value)}>
          </input>
        </li>
        <li>
          <label htmlFor="category">
            Category:
          </label>
          <input type="text" name="category" value={category}  id="category" onChange={(e) => setCategory(e.target.value)}>
          </input>
        </li>

        <li>
          <label htmlFor="description">
            Description:
          </label>
          <textarea type="text" name="description" value={description} id="description" onChange={(e) => setDescription(e.target.value)}>
          </textarea>
        </li>
        <li>
          <button type="submit" className="button primary">{ id ? "Update": "Create new product"}</button>
        </li>
        <li>
          <button type="button" onClick={() => setModalVisable(false)} className="button secondary">Back</button>
        </li>
      </ul>
      </form>
    </div>
  }
      <div className="product-list">
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Category</th>
              <th>Brand</th>
              <th>Action</th>
             </tr>
          </thead>
          <tbody>
          {products.map(product => (
            <tr>
              <td>{product._id}</td>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.category}</td>
              <td>{product.brand}</td>
              <td>
                <button onClick={openModal}>Edit </button>
                <button onClick = {() => deleteHandler(product)}>Delete </button>
              </td>
             </tr> ))}
          </tbody>
        </table>
      </div>

</div>
}
export default ProductsScreen;
