import React from 'react';
import data from './data';
import {BrowserRouter, Route, Link} from 'react-router-dom';
import './App.css';
import HomeScreen from './Screens/HomeScreen';
import ProductScreen from './Screens/ProductScreen';

function App() {
  const openMenu = () => {
     document.querySelector(".sidebar").classList.add("open");
  }
  const closeMenu = () => {
      document.querySelector(".sidebar").classList.remove("open");
  }
  return (
  <BrowserRouter>
    <div className="grid-container">
        <header className= "header">
          <div className= "brand">
          <button onClick={openMenu}>
            &#9776;
          </button>
          <Link to="/">Homeyman Designs</Link>
              <img src="/images/logo.jpg" alt="company logo"/>
           </div>
          <div className="header-links">
            <a href="cart.html">Cart</a>
            <a href="signin.html">Sign In</a>
          </div>
        </header>

        <aside className="sidebar">
          <h3>Shopping categories</h3>
          <button className="sidebar-close" onClick={closeMenu}>x</button>
          <ul>
            <li>
              <a href="products.html">Lighting</a>
            </li>
            <li>
              <a href="products.html">Mirrors</a>
            </li>
            <li>
              <a href="products.html">Rugs</a>
            </li>
            <li>
              <a href="products.html">Coffee table</a>
            </li>
          </ul>
        </aside>


      <main className="main">
        <div class = "content">
        <Route path="/product/:id" component={ProductScreen}/>
        <Route path="/" exact={true} component={HomeScreen}/>

      </div>
    </main>
    <footer className= "footer">
      <p>All rights reserved</p>
    </footer>
  </div>
  </BrowserRouter>

  );
}

export default App;
